import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';


@Injectable({
  providedIn: 'root'
})
export class DataService {

  private apiUrl = 'http://localhost/phpulse/index.php';  // URL de la API

  constructor(private http: HttpClient) { }

  // Método para obtener datos (GET)
  getData(): Observable<any> {
    return this.http.get<any>(this.apiUrl);  // Hacer la solicitud GET
  }

  // Método para enviar datos (POST)
  postData(data: any): Observable<any> {
    return this.http.post<any>(this.apiUrl, data);  // Hacer la solicitud POST
  }
}
